(defn files [path]
  (->> path
       io/file
       file-seq
       (filter #(and (fs/file? %)
                     (= ".xml" (fs/extension %))
                     (re-matches #"\d+" (fs/base-name % true))))
       (map #(vector (fs/base-name % true) %))))
